// Export helper function to get card meaning
export function getCardMeaning(cardName, orientation = 'upright') {
  const card = CARD_MEANINGS[cardName];
  if (!card) {
    return `Meaning for ${cardName} not yet available.`;
  }
  return card[orientation] || `${orientation} meaning for ${cardName} not yet available.`;
}

// Export list of all cards for iteration
export const ALL_CARDS = Object.keys(CARD_MEANINGS);
